(313, '1106055', 22, 131, 50, 'JOVEES FOOT CREAM & SCRUB-50G', '<p><span style=\"color: #333333; font-family: Montserrat, sans-serif;\">Your feet keep you grounded and take you places. Invest in their care and nourishment and your feet will thank you. Get this 2-in-1 cream and scrub to heal and repair damaged skin around your feet.</span></p>\r\n<div class=\"note \" style=\"font-family: Montserrat, sans-serif; display: inline-block; padding-right: 15px;\"><strong>PARABEN FREE</strong></div>\r\n<div class=\"note \" style=\"font-family: Montserrat, sans-serif; display: inline-block; padding-right: 15px;\"><strong>ALCOHOL FREE</strong></div>\r\n<div class=\"note \" style=\"font-family: Montserrat, sans-serif; display: inline-block; padding-right: 15px;\"><strong>CRUELTY FREE</strong></div>', '', '', '<p><span style=\"font-family: Montserrat, sans-serif; text-align: center;\">Take a sufficient quantity of cream cum scrub from the tube and massage into heels and soles of the feet in a circular motion till absorbed. Leave on.</span><br style=\"font-family: Montserrat, sans-serif; text-align: center; background-color: #ffe9c3;\" /><span style=\"font-family: Montserrat, sans-serif; text-align: center;\">For best results, use before sleeping and leave it on overnight.</span></p>', '490.00', '0.00', 3, 1, '8906007676748', 'FOOT CREAM ', '', 'FOOTCREAM', 'FOOT CREAM', 0, 0, '2020-08-25 14:08:57', 0),
(314, '1106047', 22, 113, 50, 'JOVEES EYE CONTOUR CREAM-20G', '<p><span style=\"color: #333333; font-family: Montserrat, sans-serif;\">Specially crafted with the perfect blend of peptides, plant derivatives, and herbs for extra-sensitive under-eye skin, this cream minimises dark circles and puffiness.</span></p>\r\n<div class=\"note \" style=\"font-family: Montserrat, sans-serif; display: inline-block; padding-right: 15px;\"><strong>PARABEN FREE</strong></div>\r\n<div class=\"note \" style=\"font-family: Montserrat, sans-serif; display: inline-block; padding-right: 15px;\"><strong>ALCOHOL FREE</strong></div>\r\n<div class=\"note \" style=\"font-family: Montserrat, sans-serif; display: inline-block; padding-right: 15px;\"><strong>CRUELTY FREE</strong></div>', '', '', '<p><span style=\"font-family: Montserrat, sans-serif; text-align: center;\">Cleanse your face with a face wash a toner.</span><br style=\"font-family: Montserrat, sans-serif; text-align: center; background-color: #ffe9c3;\" /><span style=\"font-family: Montserrat, sans-serif; text-align: center;\">Take a very small quantity of the eye-cream on your fingertip and massage very gently around the eye.</span><br style=\"font-family: Montserrat, sans-serif; text-align: center; background-color: #ffe9c3;\" /><span style=\"font-family: Montserrat, sans-serif; text-align: center;\">Use twice daily, in the morning as well as before going to bed.</span></p>', '850.00', '0.00', 3, 1, '8906007675581', 'EYE CREAM', '', 'EYECREAM', 'EYE CREAM', 2, 0, '2020-08-25 16:33:21', 0),
(315, '1106077', 22, 113, 50, 'JOVEES GOLD EYE CONTOUR GEL 20G', '<p><span style=\"color: #333333; font-family: Montserrat, sans-serif;\">A luxe gold and ultra-radiant experience for your beautiful eyes, this cream works to remove dark circles and makes the under-eye skin glowing and bright.</span></p>\r\n<div class=\"note \" style=\"font-family: Montserrat, sans-serif; display: inline-block; padding-right: 15px;\"><strong>PARABEN FREE</strong></div>\r\n<div class=\"note \" style=\"font-family: Montserrat, sans-serif; display: inline-block; padding-right: 15px;\"><strong>ALCOHOL FREE</strong></div>\r\n<div class=\"note \" style=\"font-family: Montserrat, sans-serif; display: inline-block; padding-right: 15px;\"><strong>CRUELTY FREE</strong></div>', '', '', '<p><span style=\"font-family: Montserrat, sans-serif; text-align: center;\">Cleanse your face with a face wash a toner.</span><br style=\"font-family: Montserrat, sans-serif; text-align: center; background-color: #ffe9c3;\" /><span style=\"font-family: Montserrat, sans-serif; text-align: center;\">Take a very small quantity of the eye-cream on your fingertip and massage very gently around the eye.</span><br style=\"font-family: Montserrat, sans-serif; text-align: center; background-color: #ffe9c3;\" /><span style=\"font-family: Montserrat, sans-serif; text-align: center;\">Use twice daily, in the morning as well as before going to bed.</span></p>', '1420.00', '0.00', 3, 1, '8906007670111', 'EYE GEL', '', 'EYEGEL', 'EYE GEL', 6, 0, '2020-08-25 16:44:13', 0),
(316, '1106074', 22, 45, 50, 'JOVEES GOLD ULTRA RADIANCE CLEANSER-50G', '<div class=\"details\" style=\"color: #333333; font-family: Montserrat, sans-serif;\">\r\n<div class=\"details-description\">\r\n<p style=\"margin-top: 0rem; margin-bottom: 1rem;\">After a long day of looking gorgeous, your skin needs to be cleansed thoroughly. Indulge in the radiance of 24K gold dust and pure gold leaves to deep cleanse your pores while maintaining the moisture balance of your skin. Follow it up with the Ultra-Radiance 24K Gold Face Wash for a double cleanse.</p>\r\n<div class=\"note \" style=\"display: inline-block; color: #000000; padding-right: 15px;\"><strong>PARABEN FREE</strong></div>\r\n<div class=\"note \" style=\"display: inline-block; color: #000000; padding-right: 15px;\"><strong>ALCOHOL FREE</strong></div>\r\n<div class=\"note \" style=\"display: inline-block; color: #000000; padding-right: 15px;\"><strong>CRUELTY FREE</strong></div>\r\n</div>\r\n</div>\r\n<div class=\"product-add-form\" style=\"clear: both; padding-top: 20px; color: #333333; font-family: Montserrat, sans-serif;\"><form id=\"product_addtocart_form\" style=\"-webkit-tap-highlight-color: rgba(0, 0, 0, 0);\" action=\"https://www.jovees.com/checkout/cart/add/uenc/aHR0cHM6Ly93d3cuam92ZWVzLmNvbS8yNGstZ29sZC11bHRyYS1yYWRpYW5jZS1jbGVhbnNlci5odG1s/product/64/\" method=\"post\" novalidate=\"novalidate\" data-product-sku=\"JOV24RC21\">\r\n<div id=\"product-options-wrapper\" class=\"product-options-wrapper\" data-hasrequired=\"* Required Fields\">\r\n<div class=\"fieldset\" style=\"border: 0px; margin: 0px 0px 20px; padding: 0px; letter-spacing: -0.31em;\" tabindex=\"0\">\r\n<div class=\"swatch-opt\" style=\"letter-spacing: normal; margin: 20px 0px;\" data-role=\"swatch-options\"> </div>\r\n</div>\r\n</div>\r\n</form></div>', '', '<p>MARIGOLD OIL</p>\r\n<p>The anti-inflammatory and anti-bacterial properties of Marigold Oil reduce redness, calm itchiness, and prevent breakouts.</p>\r\n<p>OLIVE OIL</p>\r\n<p>Olive Oil moisturises, hydrates, and removes acne-causing bacteria.</p>\r\n<p>WHEAT GERM OIL</p>\r\n<p>Wheat Germ Oil contains Vitamins B6 and E, making it a good antioxidant which boosts skin\'s health, reduces redness, and prevents early signs of ageing.</p>', '<p><span style=\"font-family: Montserrat, sans-serif; text-align: center;\">Take a small quantity on your fingertips.</span><br style=\"font-family: Montserrat, sans-serif; text-align: center; background-color: #ffe9c3;\" /><span style=\"font-family: Montserrat, sans-serif; text-align: center;\">Gently massage on your face and neck using upward and outward strokes for 2-3 minutes.</span><br style=\"font-family: Montserrat, sans-serif; text-align: center; background-color: #ffe9c3;\" /><span style=\"font-family: Montserrat, sans-serif; text-align: center;\">Wipe off the excess with a damp cotton pad or wash it off.</span><br style=\"font-family: Montserrat, sans-serif; text-align: center; background-color: #ffe9c3;\" /><span style=\"font-family: Montserrat, sans-serif; text-align: center;\">Pat dry and follow up with the scrub and moisturiser from the 24K Gold Ultra Radiance collection.</span></p>', '1170.00', '0.00', 3, 1, '8906007676090', 'FACE CLEANSER', '', 'FACECLEANSER', 'FACE CLEANSER', 2, 0, '2020-08-25 17:11:06', 0),
(317, '1106078', 22, 108, 50, 'JOVEES GOLD ULTRA RADIANCE MOISTURISER-50G', '<div class=\"details\" style=\"color: #333333; font-family: Montserrat, sans-serif;\">\r\n<div class=\"details-description\">\r\n<p style=\"margin-top: 0rem; margin-bottom: 1rem;\">The finishing touch for a luxe gold skincare routine, this moisturiser keeps your skin supple, youthful, and glowing. Enriched with pure gold leaves and other nourishing botanical extracts, it brightens your skin with regular use. For more effective results, use it along with other products from our 24K Gold Collection.</p>\r\n<div class=\"note \" style=\"display: inline-block; color: #000000; padding-right: 15px;\"><strong>PARABEN FREE</strong></div>\r\n<div class=\"note \" style=\"display: inline-block; color: #000000; padding-right: 15px;\"><strong>ALCOHOL FREE</strong></div>\r\n<div class=\"note \" style=\"display: inline-block; color: #000000; padding-right: 15px;\"><strong>CRUELTY FREE</strong></div>\r\n</div>\r\n</div>\r\n<div class=\"product-add-form\" style=\"clear: both; padding-top: 20px; color: #333333; font-family: Montserrat, sans-serif;\"><form id=\"product_addtocart_form\" style=\"-webkit-tap-highlight-color: rgba(0, 0, 0, 0);\" action=\"https://www.jovees.com/checkout/cart/add/uenc/aHR0cHM6Ly93d3cuam92ZWVzLmNvbS8yNGstZ29sZC11bHRyYS1yYWRpYW5jZS1tb2lzdHVyaXNlci5odG1s/product/112/\" method=\"post\" novalidate=\"novalidate\" data-product-sku=\"JOV24MS03\">\r\n<div id=\"product-options-wrapper\" class=\"product-options-wrapper\" data-hasrequired=\"* Required Fields\">\r\n<div class=\"fieldset\" style=\"border: 0px; margin: 0px 0px 20px; padding: 0px; letter-spacing: -0.31em;\" tabindex=\"0\">\r\n<div class=\"swatch-opt\" style=\"letter-spacing: normal; margin: 20px 0px;\" data-role=\"swatch-options\"> </div>\r\n</div>\r\n</div>\r\n</form></div>', '', '', '<p><span style=\"font-family: Montserrat, sans-serif; text-align: center;\">Cleanse your face with the Ultra-Radiance 24K Gold Face Wash, followed by the 24K Gold Ultra-Radiance scrub, and a toner.</span><br style=\"font-family: Montserrat, sans-serif; text-align: center; background-color: #ffe9c3;\" /><span style=\"font-family: Montserrat, sans-serif; text-align: center;\">Squeeze out a small coin-sized amount of the product on your palm.</span><br style=\"font-family: Montserrat, sans-serif; text-align: center; background-color: #ffe9c3;\" /><span style=\"font-family: Montserrat, sans-serif; text-align: center;\">Apply all over your face and neck using small circular motions.</span><br style=\"font-family: Montserrat, sans-serif; text-align: center; background-color: #ffe9c3;\" /><span style=\"font-family: Montserrat, sans-serif; text-align: center;\">For best results, use at least twice a day.</span></p>', '1430.00', '0.00', 3, 1, '8906007675819', 'FACE MOISTURISER', '', 'FACEMOISTURISER', 'FACE MOISTURISER', 2, 0, '2020-08-25 17:32:44', 0),
(318, '1106073', 22, 48, 50, 'JOVEES GOLD ULTRA RADIANCE FACE SCRUB-50G', '<div class=\"details\" style=\"color: #333333; font-family: Montserrat, sans-serif;\">\r\n<div class=\"details-description\">\r\n<p style=\"margin-top: 0rem; margin-bottom: 1rem;\">Complete your 24K luxe skincare with this unique scrub containing deep-penetrating granules. They exfoliate and cleanse the skin from deep within to reveal clearer and youthful skin with a radiant glow. Explore more products in our 24K Gold Collection and indulge in the complete luxe skincare experience.</p>\r\n<div class=\"note \" style=\"display: inline-block; color: #000000; padding-right: 15px;\"><strong>PARABEN FREE</strong></div>\r\n<div class=\"note \" style=\"display: inline-block; color: #000000; padding-right: 15px;\"><strong>ALCOHOL FREE</strong></div>\r\n<div class=\"note \" style=\"display: inline-block; color: #000000; padding-right: 15px;\"><strong>CRUELTY FREE</strong></div>\r\n</div>\r\n</div>\r\n<div class=\"product-add-form\" style=\"clear: both; padding-top: 20px; color: #333333; font-family: Montserrat, sans-serif;\"><form id=\"product_addtocart_form\" style=\"-webkit-tap-highlight-color: rgba(0, 0, 0, 0);\" action=\"https://www.jovees.com/checkout/cart/add/uenc/aHR0cHM6Ly93d3cuam92ZWVzLmNvbS8yNGstZ29sZC11bHRyYS1yYWRpYW5jZS1mYWNlLXNjcnViLmh0bWw%2C/product/81/\" method=\"post\" novalidate=\"novalidate\" data-product-sku=\"JOV24FS22\">\r\n<div id=\"product-options-wrapper\" class=\"product-options-wrapper\" data-hasrequired=\"* Required Fields\">\r\n<div class=\"fieldset\" style=\"border: 0px; margin: 0px 0px 20px; padding: 0px; letter-spacing: -0.31em;\" tabindex=\"0\">\r\n<div class=\"swatch-opt\" style=\"letter-spacing: normal; margin: 20px 0px;\" data-role=\"swatch-options\"> </div>\r\n</div>\r\n</div>\r\n</form></div>', '', '<p>ROSE WATER</p>\r\n<p>A relaxing and soothing ingredient, rose water helps maintain skin’s pH balance and controls excess oil production.</p>\r\n<p>SORBITOL</p>\r\n<p>Sorbitol prevents moisture-loss, hydrates skin, and prevents a drying effect that usually comes from exfoliating.</p>\r\n<p>GLYCERIN</p>\r\n<p>Glycerin enhances the moisture of your skin, reduces the appearance of wrinkles, acne, and leaves behind a youthful glow.</p>', '<p><span style=\"font-family: Montserrat, sans-serif; text-align: center;\">Moisten your face with a splash of water.</span><br style=\"font-family: Montserrat, sans-serif; text-align: center; background-color: #ffe9c3;\" /><span style=\"font-family: Montserrat, sans-serif; text-align: center;\">Squeeze out a small coin-sized amount of the product on your palm.</span><br style=\"font-family: Montserrat, sans-serif; text-align: center; background-color: #ffe9c3;\" /><span style=\"font-family: Montserrat, sans-serif; text-align: center;\">Gently massage into your skin for 2-3 minutes. Don’t do this more than twice a week.</span><br style=\"font-family: Montserrat, sans-serif; text-align: center; background-color: #ffe9c3;\" /><span style=\"font-family: Montserrat, sans-serif; text-align: center;\">Rinse thoroughly and pat dry. Follow it up with a toner and 24K Gold Ultra Radiance Moisturiser.</span></p>', '1350.00', '0.00', 3, 1, '8906007676106', 'FACE SCRUB', '', 'FACESCRUB', 'FACE SCRUB', 7, 0, '2020-08-25 17:37:59', 0),
(319, '1106075', 22, 106, 50, 'JOVEES GOLD ULTRA RADIANCE FACE PACK 50G', '<div class=\"details\" style=\"color: #333333; font-family: Montserrat, sans-serif;\">\r\n<div class=\"details-description\">\r\n<p style=\"margin-top: 0rem; margin-bottom: 1rem;\">Get your own personalised golden hour and pamper yourself with the luxurious gold skincare experience that comes with our 24K Gold Collection. Made from gold dust and other natural ingredients, this face pack will give you radiant, rejuvenated, and younger-looking skin when used regularly with the complete routine.</p>\r\n<div class=\"note \" style=\"display: inline-block; color: #000000; padding-right: 15px;\"><strong>PARABEN FREE</strong></div>\r\n<div class=\"note \" style=\"display: inline-block; color: #000000; padding-right: 15px;\"><strong>ALCOHOL FREE</strong></div>\r\n<div class=\"note \" style=\"display: inline-block; color: #000000; padding-right: 15px;\"><strong>CRUELTY FREE</strong></div>\r\n</div>\r\n</div>\r\n<div class=\"product-add-form\" style=\"clear: both; padding-top: 20px; color: #333333; font-family: Montserrat, sans-serif;\"><form id=\"product_addtocart_form\" style=\"-webkit-tap-highlight-color: rgba(0, 0, 0, 0);\" action=\"https://www.jovees.com/checkout/cart/add/uenc/aHR0cHM6Ly93d3cuam92ZWVzLmNvbS8yNGstZ29sZC11bHRyYS1yYWRpYW5jZS1mYWNlLXBhY2suaHRtbA%2C%2C/product/143/\" method=\"post\" novalidate=\"novalidate\" data-product-sku=\"JOV24FP01\">\r\n<div id=\"product-options-wrapper\" class=\"product-options-wrapper\" data-hasrequired=\"* Required Fields\">\r\n<div class=\"fieldset\" style=\"border: 0px; margin: 0px 0px 20px; padding: 0px; letter-spacing: -0.31em;\" tabindex=\"0\">\r\n<div class=\"swatch-opt\" style=\"letter-spacing: normal; margin: 20px 0px;\" data-role=\"swatch-options\"> </div>\r\n</div>\r\n</div>\r\n</form></div>', '', '', '<p><span style=\"font-family: Montserrat, sans-serif; text-align: center;\">Cleanse your face with the Ultra Radiance 24K Gold Face Wash and pat dry.</span><br style=\"font-family: Montserrat, sans-serif; text-align: center; background-color: #ffe9c3;\" /><span style=\"font-family: Montserrat, sans-serif; text-align: center;\">Squeeze out a walnut-sized amount of the face pack on your palm or in a container.</span><br style=\"font-family: Montserrat, sans-serif; text-align: center; background-color: #ffe9c3;\" /><span style=\"font-family: Montserrat, sans-serif; text-align: center;\">Apply a thick layer on your face and neck and leave it on for 20 minutes.</span><br style=\"font-family: Montserrat, sans-serif; text-align: center; background-color: #ffe9c3;\" /><span style=\"font-family: Montserrat, sans-serif; text-align: center;\">Rinse thoroughly with plain water. Pat dry and follow it up with the 24K Gold Ultra-Radiance Moisturiser.</span></p>', '1320.00', '0.00', 3, 1, '8906007675840', 'FACE PACK', '', 'FACEPACK', 'FACE PACK', 14, 1, '2020-08-25 18:14:51', 0),
(320, '1106076', 22, 108, 50, 'JOVEES GOLD ULTRA RADIANCE MASSAGE GEL-50G', '<p><span style=\"color: #333333; font-family: Montserrat, sans-serif;\">Massage your way to soft skin and a radiant glow and get regenerated skin cells, reduced lines and wrinkles, and improved blood circulation. This Massage Gel penetrates the skin easily and helps to delay the formation of lines and wrinkles. It’s a powerful rejuvenating massage gel that also restores softness, smoothness, and radiance.</span></p>\r\n<div class=\"note \" style=\"font-family: Montserrat, sans-serif; display: inline-block; padding-right: 15px;\"><strong>PARABEN FREE</strong></div>\r\n<div class=\"note \" style=\"font-family: Montserrat, sans-serif; display: inline-block; padding-right: 15px;\"><strong>ALCOHOL FREE</strong></div>\r\n<div class=\"note \" style=\"font-family: Montserrat, sans-serif; display: inline-block; padding-right: 15px;\"><strong>CRUELTY FREE</strong></div>', '', '', '<p><span style=\"font-family: Montserrat, sans-serif; text-align: center;\">Cleanse your face using the Ultra-Radiance 24K Gold Face Wash and pat dry. Follow it up with a toner.</span><br style=\"font-family: Montserrat, sans-serif; text-align: center; background-color: #ffe9c3;\" /><span style=\"font-family: Montserrat, sans-serif; text-align: center;\">Take some massage gel on your palm and massage it gently on the face using upward and outward strokes.</span><br style=\"font-family: Montserrat, sans-serif; text-align: center; background-color: #ffe9c3;\" /><span style=\"font-family: Montserrat, sans-serif; text-align: center;\">Use once a day for youthful and flawless skin. Use 24K Gold Ultra-Radiance Moisturiser after the massage.</span></p>', '2740.00', '0.00', 3, 1, '8906007675833', 'MASSAGE GEL', '', 'MASSAGEGEL', 'MASSAGE GEL', 22, 0, '2020-08-25 18:38:42', 0),
(321, '1106084', 22, 164, 50, 'JOVEES PIGMENTATION,BLEMISH MINI FACIAL KIT', '<p><span style=\"color: #333333; font-family: Montserrat, sans-serif;\">A special kit to clear out dark spots, sun spots, age spots, melasma, and hyperpigmentation, this value kit is a complete routine in itself. Included in this kit: smaller packs of Citrus and Blackberry Cleanser, Veg Oat Peel, Wheat germ with Vitamin-E Face Massage Cream, Witch Hazel and Basil Skin Toning Gel, Nutmeg and Rosemary Anti Pigmentation Blemish Face Mask, and Nutmeg and Rosemary Anti Pigmentation Blemish Face Mask.</span></p>\r\n<div class=\"note \" style=\"font-family: Montserrat, sans-serif; display: inline-block; padding-right: 15px;\"><strong>PARABEN FREE</strong></div>\r\n<div class=\"note \" style=\"font-family: Montserrat, sans-serif; display: inline-block; padding-right: 15px;\"><strong>ALCOHOL FREE</strong></div>\r\n<div class=\"note \" style=\"font-family: Montserrat, sans-serif; display: inline-block; padding-right: 15px;\"><strong>CRUELTY FREE</strong></div>', 'ANTI PIGMENTATION AND BLEMISHES', '', '<p><span style=\"font-family: Montserrat, sans-serif; text-align: center;\">Wash and cleanse your face with the Cleanser. Massage it gently on your face for 2-3 minutes and wash thoroughly.</span><br style=\"font-family: Montserrat, sans-serif; text-align: center; background-color: #ffe9c3;\" /><span style=\"font-family: Montserrat, sans-serif; text-align: center;\">Use the scrub as the next step. Moisten face.</span><br style=\"font-family: Montserrat, sans-serif; text-align: center; background-color: #ffe9c3;\" /><span style=\"font-family: Montserrat, sans-serif; text-align: center;\">Apply all over the face and neck. Massage gently with fingertips for 2 to 3 minutes in circular motion.</span><br style=\"font-family: Montserrat, sans-serif; text-align: center; background-color: #ffe9c3;\" /><span style=\"font-family: Montserrat, sans-serif; text-align: center;\">Wash with plain water or remove with m