(529, '1133012', 22, 98, 66, 'SALON PROFESSIONAL HAIR SPRAY', '<p><span style=\"color: #333333; font-family: \'Amazon Ember\', Arial, sans-serif; font-size: 12pt;\">Minuet Hairspray Professional Extra Hold 75ml, with its micro fine spray, gives long lasting and reliable hold for up to 24 hours and is easy to brush out. Long Lasting Hair Spray Wholesale Online. Minuet\'s Hairspray helps to protect your hair from damage, keeping it looking natural and healthy, so you\'re the only one that know its there.</span></p>', '', '', '', '495.00', '0.00', 6, 1, '8719281997207', 'SALON PROFESSIONAL HAIR SPRAY', '', 'SALONPROFESSIONALHAIRSPRAY', 'SALON PROFESSIONAL HAIR SPRAY', 1, 0, '2020-11-11 16:36:30', 0),
(530, '1135022', 22, 98, 66, 'SALON STYLING MOUSSE', '<p><span style=\"color: #6d7680; font-family: Verdana, sans-serif; font-size: 16px; text-align: center;\">After shampooing, dry hair with towel. Shake the can before using the mousse.  Hold the can downwards and dispense into the palm of hand. Scatter equally the mousse to the hair. Style hair.</span></p>', '', '', '', '750.00', '0.00', 6, 1, '8719281103417', 'SALON STYLING MOUSSE', '', 'SALONSTYLINGMOUSSE', 'SALON STYLING MOUSSE', 0, 0, '2020-11-11 16:44:54', 0),
(531, '1135001', 22, 98, 64, 'LAFRESH HAIR MOUSSE', '<p><span style=\"color: #858484; font-family: \'Roboto Condensed\'; font-size: 12pt;\">Creates the desired hairstyle easily and quickly.</span><br style=\"margin: 0px; padding: 0px; outline: none; color: #858484; font-family: \'Roboto Condensed\';\" /><span style=\"color: #858484; font-family: \'Roboto Condensed\'; font-size: 12pt;\">Hard holding power.</span><br style=\"margin: 0px; padding: 0px; outline: none; color: #858484; font-family: \'Roboto Condensed\';\" /><span style=\"color: #858484; font-family: \'Roboto Condensed\'; font-size: 12pt;\">Quick dry.</span><br style=\"margin: 0px; padding: 0px; outline: none; color: #858484; font-family: \'Roboto Condensed\';\" /><span style=\"color: #858484; font-family: \'Roboto Condensed\'; font-size: 12pt;\">Creates non-sticky and shiny styling.</span></p>', '', '', '', '650.00', '0.00', 6, 1, '5060071227176', 'LAFRESH HAIR MOUSSE', '', 'LAFRESHHAIRMOUSSE', 'LAFRESH HAIR MOUSSE', 1, 0, '2020-11-11 16:50:11', 0),
(532, '1135020', 22, 98, 61, 'LOREAL STYLISTE MOUSSE CERAMIDE R', '<p><span style=\"color: #848484; font-family: Arial; font-size: 12pt;\"> </span><span style=\"color: #333333; font-family: \'Amazon Ember\', Arial, sans-serif; font-size: small;\">L\'Oréal haircare laboratories presents Elvive Styliste Mousse, offering an impressive combination of care and hold. The Science of damaged hair Daily wear and tear can break up the hair\'s surface and weaken the fibre. Hair becomes damaged, looking limp and lifeless, dull and straw-like. To bring back its beauty, it needs a special boost. Replenishing for weak, limp, lifeless hair Full Restore 5 Elvive Styliste Mousse is the combination of Elvive care expertise and the experience of L\'Oréal Paris experts to create a perfect style. Enriched with Ceramide R - a replica of intercellular cement naturally present in the hair - its smooth formula is designed to: 1 Help your hair to achieve a perfect replenished look. 2 Offer a 48H* long lasting hold thanks to its strong fixing power. Create a perfect style that holds!</span></p>', '', '<p class=\"MsoNormal\"><span style=\"color: #333333; font-family: \'Amazon Ember\', Arial, sans-serif; font-size: small;\">AQUA / WATER, ISOBUTANE, PROPYLENE GLYCOL, BUTANE, PROPANE, PHENOXYETHANOL, PEG40 HYDROGENATED CASTOR OIL, TRIACETIN, AMINOMETHYL PROPANOL, TRIDECETH12, POLYQUATERNIUM4, POLYSORBATE 20, BENZOPHENONE4, BENZYL SALICYLATE, LINALOOL, AMODIMETHICONE, ALPHAISOMETHYL IONONE, ACRYLATES COPOLYMER, METHYLPARABEN, BUTYLPHENYL METHYLPROPIONAL, CETRIMONIUM CHLORIDE, CITRONELLOL, HEXYL CINNAMAL, PARFUM / FRAGRANCE.</span></p>', '<p><span style=\"color: #848484; font-family: Arial; font-size: 12pt;\">L\'Oreal Paris expert advice: Shake well and turn upside down to dispense 2-3 balls of mousse into your hand. To achieve maximum volume work into the hair from roots to tips, dry with your head upside down using a round brush while blow-drying.</span></p>', '1590.00', '0.00', 6, 1, '5011408004090', 'LOREAL STYLISTE MOUSSE CERAMIDE R ', '', 'LOREALSTYLISTEMOUSSECERAMIDER', 'LOREAL STYLISTE MOUSSE  CERAMIDE R', 0, 0, '2020-11-11 16:58:58', 0),
(533, '1135019', 22, 98, 61, 'LOREAL STYLISTE MOUSSE COLLAGEN', '', '', '', '', '1590.00', '0.00', 6, 1, '8719281103554', 'LOREAL STYLISTE MOUSSE COLLAGEN', '', 'LOREALSTYLISTEMOUSSECOLLAGEN', 'LOREAL STYLISTE MOUSSE COLLAGEN', 1, 0, '2020-11-11 17:11:33', 0),
(534, '1133014', 22, 98, 61, 'LOREAL ELNETT HAIR SPRAY', '<h2 style=\"box-sizing: inherit; margin: 0px 0px 15px; line-height: 1.35; color: #777777; font-family: \'Open Sans\', Arial, Helvetica, sans-serif; letter-spacing: 0.07px; outline: none !important; font-size: 14px !important; font-weight: 500 !important;\">L\'Oreal Paris Elnett Satin Hairspray Supreme Hold Extravagant Volume - 75ml<br />Our finest hairspray used the finest stylists.<br />The legendary, long lasting hold of Elnett.<br />Now in a texturising formula.<br />Dramatically texturises hair fibres from the root, to give excessive body and backcombed-like volume to the hair.<br />Disappears at the stroke of a brush.</h2>\r\n<p style=\"box-sizing: inherit; margin: 0px 0px 15px; line-height: 27px; letter-spacing: 0.07px; color: #777777; font-family: \'Open Sans\', Arial, Helvetica, sans-serif; outline: none !important;\">&nbsp;</p>', '', '', '', '1050.00', '0.00', 6, 1, '3600522670346', 'LOREAL ELNETT HAIR SPRAY', '', 'LOREALELNETTHAIRSPRAY', 'LOREAL ELNETT HAIR SPRAY', 1, 0, '2020-11-11 17:19:44', 0),
